function modoEscuro(){var o=document.getElementById("darkmode"),e=document.body;o.addEventListener("click",function(){e.classList.toggle("dark-mode")})}function animacaoSections(){var o=document.querySelectorAll("section"),e=new IntersectionObserver(function(o){o.forEach(function(o){o.isIntersecting&&o.target.classList.add("show")})});o.forEach(function(o){return e.observe(o)})}modoEscuro(),animacaoSections();